import React, { useState } from "react";
import "./AddProductForm.css"; // Import your CSS file

const AddProductForm = ({ onSubmit }) => {
  // State declarations for form inputs
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  const [quantity, setQuantity] = useState("");

  const handleSubmit = (e) => {
      e.preventDefault(); // Prevent default form submission
      // Create a new product object with the form input values
      const newProduct = {
        name: name,
        description: description,
        price: price,
        quantity: quantity
      };
    // Call the onSubmit function passed from the parent component
    onSubmit(newProduct);
    // Reset the form input values
    setName("");
    setDescription("");
    setPrice("");
    setQuantity("");
  };

  return (
    <div className="overlay">
      <div className="modal">
        <h2>Add New Product</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={name}
            onChange={(e) => setNameLocal(e.target.value)}
          />
          <label htmlFor="description">Description:</label>
          <input
            type="text"
            id="description"
            name="description"
            value={description}
            onChange={(e) => setDescriptionLocal(e.target.value)}
          />
          <label htmlFor="price">Price:</label>
          <input
            type="text"
            id="price"
            name="price"
            value={price}
            onChange={(e) => setPriceLocal(e.target.value)}
          />
          <label htmlFor="quantity">Quantity:</label>
          <input
            type="text"
            id="quantity"
            name="quantity"
            value={quantity}
            onChange={(e) => setQuantityLocal(e.target.value)}
          />
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}

export default AddProductForm;
