import React, { useState } from "react";
import Modal from "react-modal"; // Import the modal library
import axios from "axios"; // Import Axios for HTTP requests
import dummyData from "../../dummyData"; // Import dummy data
import "./Products.css"; // Importing the CSS file

const Products = () => {
  const [products, setproducts] = useState({id:"",name:"",description:"",price:"",quantity:""}); // State for products

  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = async() => {
    const response = await axios.post('http://localhost:8082/api/products/searchProducts?productId=2&userId=1');
    console.log(response);
    const filtered = products.filter((item) => item.id == searchTerm);
    setproducts(filtered);
  };
  return (
    <div>
      <h2>products</h2>
      <div className="search-container">
        <input
          type="text"
          className="search-input"
          placeholder="Search by id"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="search-button" onClick={handleSearch}>
          Search
        </button>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Description</th>
            <th>price</th>
            <th>quantity</th>
          </tr>
        </thead>
        <tbody>
          {products.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.description}</td>
              <td>{item.price}</td>
              <td>{item.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Products;
