import React, { useState } from "react";
import Modal from "react-modal"; // Import the modal library
import axios from "axios"; // Import Axios for HTTP requests
import dummyData from "../../dummyData"; // Import dummy data
import "./Products.css"; // Importing the CSS file
import AddProductForm from "./AddProductForm";

const Products = () => {
  const [products, setproducts] = useState(dummyData.products); // State for products

  const [searchTerm, setSearchTerm] = useState("");

  const [showAddForm, setShowAddForm] = useState(false);

  const handleSearch = () => {
    //TODO : API request
    const filtered = products.filter((item) => item.id == searchTerm);
    setproducts(filtered);
  };
  const handleDelete = (id) => {
    //TODO : API request
    alert(id);
    //setproducts(filtered);
  };

  const handleAddProduct = () => {
    // Implement the logic to add a new product
    // For example:
    // const newProduct = { id: 'new_id', name: 'New Product', description: 'Description', price: 0, quantity: 0 };
    // setProducts([...products, newProduct]);
    setShowAddForm(true);
  };
  const handleSubmitProduct = (newProduct) => {
    // Implement the logic to add the new product to your list of products
    // For example:
    // setProducts([...products, newProduct]);
    console.log(newProduct);
    setShowAddForm(false); // Close the form after submission
  };
  return (
    <div>
      <h2>products</h2>
      <div className="search-container">
        <input
          type="text"
          className="search-input"
          placeholder="Search by id"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="search-button" onClick={handleSearch}>
          Search
        </button>
      </div>
      <div>
        <button className="add-button" onClick={handleAddProduct}>
          Add New Product
        </button>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Description</th>
            <th>price</th>
            <th>quantity</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {products.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.description}</td>
              <td>{item.price}</td>
              <td>{item.quantity}</td>
              <td>
                <button
                  className="delete-button"
                  onClick={() => {
                    handleDelete(item.id);
                  }}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {showAddForm && <AddProductForm onSubmit={handleSubmitProduct} />}
    </div>
  );
};

export default Products;
