import React, { useState } from "react";
import "./AddProductForm.css"; // Import your CSS file

function AddProductForm({ onSubmit, setName, setDescription, setPrice, setQuantity }) {
  const [name, setNameLocal] = useState("");
  const [description, setDescriptionLocal] = useState("");
  const [price, setPriceLocal] = useState("");
  const [quantity, setQuantityLocal] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({ name, description, price, quantity });
    setName(name); // Update the state using the setName function passed from props
    setDescription(description);
    setPrice(price);
    setQuantity(quantity);
    setNameLocal(""); // Reset the local state
    setDescriptionLocal("");
    setPriceLocal("");
    setQuantityLocal("");
  };

  return (
    <div className="overlay">
      <div className="modal">
        <h2>Add New Product</h2>
        <form onSubmit={handleSubmit}>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={name}
            onChange={(e) => setNameLocal(e.target.value)}
          />
          <label htmlFor="description">Description:</label>
          <input
            type="text"
            id="description"
            name="description"
            value={description}
            onChange={(e) => setDescriptionLocal(e.target.value)}
          />
          <label htmlFor="price">Price:</label>
          <input
            type="text"
            id="price"
            name="price"
            value={price}
            onChange={(e) => setPriceLocal(e.target.value)}
          />
          <label htmlFor="quantity">Quantity:</label>
          <input
            type="text"
            id="quantity"
            name="quantity"
            value={quantity}
            onChange={(e) => setQuantityLocal(e.target.value)}
          />
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}

export default AddProductForm;
